import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 8.01-223.2 Immunity of persons for statements made at public hearing or communicated to third party.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-223.2.md","filePath":"vacode/8.01-223.2.md"}'),o={name:"vacode/8.01-223.2.md"},i=r('<h1 id="§-8-01-223-2-immunity-of-persons-for-statements-made-at-public-hearing-or-communicated-to-third-party" tabindex="-1">§ 8.01-223.2 Immunity of persons for statements made at public hearing or communicated to third party. <a class="header-anchor" href="#§-8-01-223-2-immunity-of-persons-for-statements-made-at-public-hearing-or-communicated-to-third-party" aria-label="Permalink to &quot;§ 8.01-223.2 Immunity of persons for statements made at public hearing or communicated to third party.&quot;">​</a></h1><p>A. A person shall be immune from civil liability for a violation of § <a href="/vacode/18.2-499/">18.2-499</a>, a claim of tortious interference with an existing contract or a business or contractual expectancy, or a claim of defamation based solely on statements (i) regarding matters of public concern that would be protected under the First Amendment to the United States Constitution made by that person that are communicated to a third party or (ii) made at a public hearing before the governing body of any locality or other political subdivision, or the boards, commissions, agencies and authorities thereof, and other governing bodies of any local governmental entity concerning matters properly before such body. The immunity provided by this section shall not apply to any statements made with actual or constructive knowledge that they are false or with reckless disregard for whether they are false.</p><p>B. Any person who has a suit against him dismissed or a witness subpoena or subpoena duces tecum quashed pursuant to the immunity provided by this section may be awarded reasonable attorney fees and costs.</p><p>2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0798" target="_blank" rel="noreferrer">798</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0239" target="_blank" rel="noreferrer">239</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0586" target="_blank" rel="noreferrer">586</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0597" target="_blank" rel="noreferrer">597</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0824" target="_blank" rel="noreferrer">824</a>.</p>',4),n=[i];function s(c,l,d,m,h,p){return t(),a("div",null,n)}const g=e(o,[["render",s]]);export{u as __pageData,g as default};
