import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 8.01-225.02 Certain liability protection for health care providers during disasters.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-225.02.md","filePath":"vacode/8.01-225.02.md"}'),i={name:"vacode/8.01-225.02.md"},o=t('<h1 id="§-8-01-225-02-certain-liability-protection-for-health-care-providers-during-disasters" tabindex="-1">§ 8.01-225.02 Certain liability protection for health care providers during disasters. <a class="header-anchor" href="#§-8-01-225-02-certain-liability-protection-for-health-care-providers-during-disasters" aria-label="Permalink to &quot;§ 8.01-225.02 Certain liability protection for health care providers during disasters.&quot;">​</a></h1><p>A. In the absence of gross negligence or willful misconduct, any health care provider who responds to a disaster shall not be liable for any injury or wrongful death of any person arising from the delivery or withholding of health care when (i) a local emergency, state of emergency, or public health emergency has been or is subsequently declared in response to such disaster, and (ii) the emergency and subsequent conditions caused a lack of resources, attributable to the disaster, rendering the health care provider unable to provide the level or manner of care that otherwise would have been required in the absence of the emergency and which resulted in the injury or wrongful death at issue.</p><p>B. For purposes of this section:</p><p>&quot;Communicable disease of public health threat&quot; has the same definition as provided in § <a href="/vacode/44-146.16/">44-146.16</a>.</p><p>&quot;Disaster&quot; means any &quot;disaster,&quot; &quot;emergency,&quot; or &quot;major disaster&quot; as those terms are used and defined in § <a href="/vacode/44-146.16/">44-146.16</a>.</p><p>&quot;Health care provider&quot; has the same definition as provided in § <a href="/vacode/8.01-581.1/">8.01-581.1</a>.</p><p>&quot;Local emergency&quot; has the same definition as provided in § <a href="/vacode/44-146.16/">44-146.16</a>.</p><p>&quot;Public health emergency&quot; has the same definition as provided in § <a href="/vacode/8.01-225.01/">8.01-225.01</a>.</p><p>&quot;Resource shortage&quot; has the same definition as provided in § <a href="/vacode/44-146.16/">44-146.16</a>.</p><p>&quot;State of emergency&quot; has the same definition as provided in § <a href="/vacode/44-146.16/">44-146.16</a>.</p><p>2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0121" target="_blank" rel="noreferrer">121</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0157" target="_blank" rel="noreferrer">157</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0617" target="_blank" rel="noreferrer">617</a>.</p>',11),n=[o];function s(d,h,c,l,p,u){return a(),r("div",null,n)}const v=e(i,[["render",s]]);export{g as __pageData,v as default};
