import{_ as a,o as e,c as i,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 8.01-226.13 Limited standing to seek injunctive relief against manufacturing companies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-226.13.md","filePath":"vacode/8.01-226.13.md"}'),n={name:"vacode/8.01-226.13.md"},o=t('<h1 id="§-8-01-226-13-limited-standing-to-seek-injunctive-relief-against-manufacturing-companies" tabindex="-1">§ 8.01-226.13 Limited standing to seek injunctive relief against manufacturing companies. <a class="header-anchor" href="#§-8-01-226-13-limited-standing-to-seek-injunctive-relief-against-manufacturing-companies" aria-label="Permalink to &quot;§ 8.01-226.13 Limited standing to seek injunctive relief against manufacturing companies.&quot;">​</a></h1><p>A. As used in this section:</p><p>&quot;Manufacturing company&quot; means a domestic or foreign corporation primarily engaged in activities that, in accordance with the North American Industrial Classification System (NAICS), United States Manual, United States Office of Management and Budget, 2012 Edition, would be included in Sector 31, 32, or 33.</p><p>&quot;Public greenway&quot; means any system of hiking, biking, or horseback riding trails established by a locality or political subdivision.</p><p>&quot;Public park, recreational facility, or playground&quot; means any such facility established by a locality pursuant to § <a href="http://law.lis.virginia.gov/vacode/15.2-1806/" target="_blank" rel="noreferrer">15.2-1806</a>.</p><p>B. No action shall be initiated or maintained to enjoin the continued use and operation of a manufacturing company solely on the basis of the claimant&#39;s use of a public park, recreational facility, or playground or public greenway, when such manufacturing company existed prior to the creation of such public park, recreational facility, or playground, or public greenway.</p><p>C. This section shall not limit actions brought by the Commonwealth, a locality, or an entity designated pursuant to subdivision A 3 of § <a href="http://law.lis.virginia.gov/vacode/15.2-1806/" target="_blank" rel="noreferrer">15.2-1806</a>.</p><p>2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0669" target="_blank" rel="noreferrer">669</a>.</p>',8),r=[o];function s(c,l,u,d,p,g){return e(),i("div",null,r)}const h=a(n,[["render",s]]);export{m as __pageData,h as default};
