import{_ as e,o as i,c as o,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 8.01-226.5:1 Civil immunity for school board employees supervising self-administration of certain medication.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-226.5:1.md","filePath":"vacode/8.01-226.5:1.md"}'),t={name:"vacode/8.01-226.5:1.md"},n=a('<h1 id="§-8-01-226-5-1-civil-immunity-for-school-board-employees-supervising-self-administration-of-certain-medication" tabindex="-1">§ 8.01-226.5:1 Civil immunity for school board employees supervising self-administration of certain medication. <a class="header-anchor" href="#§-8-01-226-5-1-civil-immunity-for-school-board-employees-supervising-self-administration-of-certain-medication" aria-label="Permalink to &quot;§ 8.01-226.5:1 Civil immunity for school board employees supervising self-administration of certain medication.&quot;">​</a></h1><p>A. Any school principal or other employee of a school board who, in good faith, without compensation, and in the absence of gross negligence or willful misconduct, supervises the self-administration of inhaled asthma medications or auto-injectable epinephrine by a student, pursuant to § <a href="http://law.lis.virginia.gov/vacode/22.1-274.2/" target="_blank" rel="noreferrer">22.1-274.2</a>, shall not be liable for any civil damages for acts or omissions resulting from the supervision of self-administration of inhaled asthma medications or auto-injectable epinephrine by such student. Further, no such principal or school board employee shall be liable for any civil damages for any injuries or deaths resulting from the misuse of such auto-injectable epinephrine.</p><p>B. For the purposes of this section, &quot;employee&quot; shall include any person employed by a local health department who is assigned to a public school pursuant to an agreement between a local health department and a school board.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0871" target="_blank" rel="noreferrer">871</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0785" target="_blank" rel="noreferrer">785</a>.</p>',4),r=[n];function s(l,c,d,h,p,m){return i(),o("div",null,r)}const _=e(t,[["render",s]]);export{u as __pageData,_ as default};
