import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 8.01-226.5:2 Immunity of hospital and emergency medical services agency personnel for the acceptance of certain infants.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-226.5:2.md","filePath":"vacode/8.01-226.5:2.md"}'),n={name:"vacode/8.01-226.5:2.md"},r=t('<h1 id="§-8-01-226-5-2-immunity-of-hospital-and-emergency-medical-services-agency-personnel-for-the-acceptance-of-certain-infants" tabindex="-1">§ 8.01-226.5:2 Immunity of hospital and emergency medical services agency personnel for the acceptance of certain infants. <a class="header-anchor" href="#§-8-01-226-5-2-immunity-of-hospital-and-emergency-medical-services-agency-personnel-for-the-acceptance-of-certain-infants" aria-label="Permalink to &quot;§ 8.01-226.5:2 Immunity of hospital and emergency medical services agency personnel for the acceptance of certain infants.&quot;">​</a></h1><p>Any personnel of a hospital or emergency medical services agency receiving a child under the circumstances described in the second paragraph of § <a href="/vacode/18.2-371/">18.2-371</a>, subdivision B 2 of § <a href="/vacode/18.2-371.1/">18.2-371.1</a>, or subsection B of § <a href="/vacode/40.1-103/">40.1-103</a> shall be immune from civil liability or criminal prosecution for injury or other damage to the child unless such injury or other damage is the result of gross negligence or willful misconduct by such personnel. Any hospital or emergency medical services agency that voluntarily installs a newborn safety device for the reception of children shall ensure that (i) the device is located inside the hospital or emergency medical services agency in an area that is conspicuous and visible to employees or personnel, (ii) the device is staffed 24 hours a day by a health care provider or emergency medical services personnel, (iii) the device is climate controlled and serves as a safe sleep environment for an infant, (iv) the device is equipped with a dual alarm system that sounds 60 seconds after a child is placed in the device and automatically places a call to 911 if the alarm is not deactivated within 60 seconds from within the hospital or emergency medical services agency, (v) the dual alarm system is visually checked at least two times per day and tested at least one time per week to ensure the alarm system is in working order, (vi) the device automatically locks when a child is placed in the device, and (vii) the device is identifiable by appropriate signage that shall include written and pictorial operational instructions.</p><p>2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0816" target="_blank" rel="noreferrer">816</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0822" target="_blank" rel="noreferrer">822</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0080" target="_blank" rel="noreferrer">80</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0081" target="_blank" rel="noreferrer">81</a>.</p>',3),c=[r];function s(o,l,d,h,f,p){return a(),i("div",null,c)}const v=e(n,[["render",s]]);export{g as __pageData,v as default};
