import{_ as o,o as r,c as i,O as e}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 8.01-226.8 Civil immunity for public and nonprofit corporation officials and private volunteers participating in certain programs for probationers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-226.8.md","filePath":"vacode/8.01-226.8.md"}'),a={name:"vacode/8.01-226.8.md"},t=e('<h1 id="§-8-01-226-8-civil-immunity-for-public-and-nonprofit-corporation-officials-and-private-volunteers-participating-in-certain-programs-for-probationers" tabindex="-1">§ 8.01-226.8 Civil immunity for public and nonprofit corporation officials and private volunteers participating in certain programs for probationers. <a class="header-anchor" href="#§-8-01-226-8-civil-immunity-for-public-and-nonprofit-corporation-officials-and-private-volunteers-participating-in-certain-programs-for-probationers" aria-label="Permalink to &quot;§ 8.01-226.8 Civil immunity for public and nonprofit corporation officials and private volunteers participating in certain programs for probationers.&quot;">​</a></h1><p>Probation officers; court personnel; state, county, city, and town personnel; any other public officials; and private volunteers who participate in a program where persons on probation or community service are ordered as a condition of probation or community service to pick up litter along a section of public roadway or waterway, to perform recycling duties at landfills, garbage transfer sites, and other waste disposal systems, to mow rights-of-way or to perform other landscaping maintenance tasks, or to perform services assigned by such probation officers, court personnel, state, county, city, or town personnel, or private volunteers acting as approved worksite supervisors of a court-approved voluntary jail diversion program shall not be liable for any civil damages to a probationer or person on community service, or the property of such person, for acts or omissions resulting from such participation, unless such act or omission is the result of willful misconduct. The provisions of this section shall not be interpreted to grant any immunity to a driver transporting the persons on probation or community service or a motorist who, by his negligence, may injure such probationer or person on community service.</p><p>Nonprofit corporation employees or officials who participate in a program where persons on probation or community service are ordered as a condition of probation or community service to pick up litter along a section of public roadway or waterway, to perform recycling duties at landfills, garbage transfer sites, and other waste disposal systems, to mow rights-of-way or to perform other landscaping maintenance tasks, or to perform services assigned by such nonprofit corporation employees or officials acting as approved worksite supervisors of a court-approved voluntary jail diversion program shall not be liable for any civil damages to a probationer or person on community service, or the property of such person, for acts or omissions resulting from such participation, unless such act or omission is the result of gross negligence or willful misconduct.</p><p>2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0434" target="_blank" rel="noreferrer">434</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0182" target="_blank" rel="noreferrer">182</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0688" target="_blank" rel="noreferrer">688</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0731" target="_blank" rel="noreferrer">731</a>.</p>',4),n=[t];function s(p,c,l,f,u,m){return r(),i("div",null,n)}const v=o(a,[["render",s]]);export{d as __pageData,v as default};
