import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 8.01-243.1 Actions for medical malpractice; minors.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-243.1.md","filePath":"vacode/8.01-243.1.md"}'),r={name:"vacode/8.01-243.1.md"},i=o('<h1 id="§-8-01-243-1-actions-for-medical-malpractice-minors" tabindex="-1">§ 8.01-243.1 Actions for medical malpractice; minors. <a class="header-anchor" href="#§-8-01-243-1-actions-for-medical-malpractice-minors" aria-label="Permalink to &quot;§ 8.01-243.1 Actions for medical malpractice; minors.&quot;">​</a></h1><p>Notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/8.01-229/" target="_blank" rel="noreferrer">8.01-229</a> A and except as provided in subsection C of § <a href="http://law.lis.virginia.gov/vacode/8.01-243/" target="_blank" rel="noreferrer">8.01-243</a>, any cause of action accruing on or after July 1, 1987, on behalf of a person who was a minor at the time the cause of action accrued for personal injury or death against a health care provider pursuant to Chapter 21.1 (§ <a href="http://law.lis.virginia.gov/vacode/8.01-581.1/" target="_blank" rel="noreferrer">8.01-581.1</a> et seq.) shall be commenced within two years of the date of the last act or omission giving rise to the cause of action except that if the minor was less than eight years of age at the time of the occurrence of the malpractice, he shall have until his tenth birthday to commence an action. Any minor who is ten years of age or older on or before July 1, 1987, shall have no less than two years from that date within which to commence such an action.</p><p>1987, cc. 294, 645.</p>',3),n=[i];function c(s,h,l,d,m,f){return a(),t("div",null,n)}const u=e(r,[["render",c]]);export{_ as __pageData,u as default};
