import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 8.01-277.1 Objections to personal jurisdiction or defective process; what constitutes waiver.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-277.1.md","filePath":"vacode/8.01-277.1.md"}'),r={name:"vacode/8.01-277.1.md"},n=o('<h1 id="§-8-01-277-1-objections-to-personal-jurisdiction-or-defective-process-what-constitutes-waiver" tabindex="-1">§ 8.01-277.1 Objections to personal jurisdiction or defective process; what constitutes waiver. <a class="header-anchor" href="#§-8-01-277-1-objections-to-personal-jurisdiction-or-defective-process-what-constitutes-waiver" aria-label="Permalink to &quot;§ 8.01-277.1 Objections to personal jurisdiction or defective process; what constitutes waiver.&quot;">​</a></h1><p>A. Except as provided in § <a href="http://law.lis.virginia.gov/vacode/8.01-277/" target="_blank" rel="noreferrer">8.01-277</a>, a person waives any objection to personal jurisdiction or defective process if he engages in conduct related to adjudicating the merits of the case, including, but not limited to:</p><ol><li><p>Filing a demurrer, plea in bar, answer, counterclaim, cross-claim, or third-party claim;</p></li><li><p>Conducting discovery, except as provided in subsection B;</p></li><li><p>Seeking a ruling on the merits of the case; or</p></li><li><p>Actively participating in proceedings related to determining the merits of the case.</p></li></ol><p>B. A person does not waive any objection to personal jurisdiction or defective process if he engages in conduct unrelated to adjudicating the merits of the case, including, but not limited to:</p><ol><li><p>Requesting or agreeing to an extension of time;</p></li><li><p>Agreeing to a scheduling order;</p></li><li><p>Conducting discovery authorized by the court related to adjudicating the objection;</p></li><li><p>Observing or attending proceedings in the case;</p></li><li><p>Filing a motion to transfer venue pursuant to § <a href="http://law.lis.virginia.gov/vacode/8.01-264/" target="_blank" rel="noreferrer">8.01-264</a> when such motion is filed contemporaneously with the objection; or</p></li><li><p>Removing the case to federal court.</p></li></ol><p>2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0710" target="_blank" rel="noreferrer">710</a>.</p>',6),a=[n];function s(c,l,d,p,u,h){return t(),i("div",null,a)}const f=e(r,[["render",s]]);export{v as __pageData,f as default};
