import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 8.01-327.2 Who are privileged from arrest under civil process.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-327.2.md","filePath":"vacode/8.01-327.2.md"}'),o={name:"vacode/8.01-327.2.md"},i=a('<h1 id="§-8-01-327-2-who-are-privileged-from-arrest-under-civil-process" tabindex="-1">§ 8.01-327.2 Who are privileged from arrest under civil process. <a class="header-anchor" href="#§-8-01-327-2-who-are-privileged-from-arrest-under-civil-process" aria-label="Permalink to &quot;§ 8.01-327.2 Who are privileged from arrest under civil process.&quot;">​</a></h1><p>In addition to the exemptions made by §§ <a href="http://law.lis.virginia.gov/vacode/30-4/" target="_blank" rel="noreferrer">30-4</a>, <a href="http://law.lis.virginia.gov/vacode/30-6/" target="_blank" rel="noreferrer">30-6</a>, <a href="http://law.lis.virginia.gov/vacode/30-7/" target="_blank" rel="noreferrer">30-7</a>, <a href="http://law.lis.virginia.gov/vacode/30-8/" target="_blank" rel="noreferrer">30-8</a>, <a href="http://law.lis.virginia.gov/vacode/19.2-280/" target="_blank" rel="noreferrer">19.2-280</a>, and <a href="http://law.lis.virginia.gov/vacode/44-97/" target="_blank" rel="noreferrer">44-97</a>, the following persons shall not be arrested, apprehended, or detained under any civil process during the times respectively herein set forth, but shall not otherwise be privileged from service of civil process by this section:</p><ol><li><p>The President of the United States, and the Governor of the Commonwealth at all times during their terms of office;</p></li><li><p>The Lieutenant Governor of the Commonwealth during attendance at sessions of the General Assembly and while going to and from such sessions;</p></li><li><p>Members of either house of the Congress of the United States during the session of Congress and for fifteen days next before the beginning and after the ending of any session, and during any time that they are serving on any committee or performing any other service under an order or request of either house of Congress;</p></li><li><p>A judge, grand juror or witness, required by lawful authority to attend any court or place, during such attendance and while going to and from such court or place;</p></li><li><p>Members of the National Guard while going to, attending at, or returning from, any muster or court-martial;</p></li><li><p>Ministers of the gospel while engaged in performing religious services in a place where a congregation is assembled and while going to and returning from such place; and</p></li><li><p>Voters going to, attending at, or returning from an election. Such privilege shall only be on the days of such attendance.</p></li></ol><p>1977, c. 617; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0221" target="_blank" rel="noreferrer">221</a>.</p>',4),n=[i];function s(l,d,h,c,g,f){return r(),t("div",null,n)}const v=e(o,[["render",s]]);export{u as __pageData,v as default};
