import{_ as a,o as t,c as o,z as e,a as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 8.01-37.1 Claims for medical services provided by United States; proof of reasonable value.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-37.1.md","filePath":"vacode/8.01-37.1.md"}'),s={name:"vacode/8.01-37.1.md"},i=e("h1",{id:"§-8-01-37-1-claims-for-medical-services-provided-by-united-states-proof-of-reasonable-value",tabindex:"-1"},[r("§ 8.01-37.1 Claims for medical services provided by United States; proof of reasonable value. "),e("a",{class:"header-anchor",href:"#§-8-01-37-1-claims-for-medical-services-provided-by-united-states-proof-of-reasonable-value","aria-label":'Permalink to "§ 8.01-37.1 Claims for medical services provided by United States; proof of reasonable value."'},"​")],-1),n=e("p",null,"Whenever any person sustains personal injuries caused by the alleged negligence of another, and a claim against any person alleged to be liable is created in favor of the United States under federal law (42 U.S.C. § 2651 et seq.) for the reasonable value of medical, surgical or dental care and treatment provided, the injured party may, on behalf of the United States, claim the reasonable value of the medical services provided as an element of damages in a civil action against the person alleged to be liable. It shall not be required that the United States intervene in the action or be made a party in order to establish its claim. A sworn written statement of the authorized representative of the department or agency providing such services prepared in accordance with the regulations promulgated pursuant to 42 U.S.C. § 2652 shall be admissible as evidence of the reasonable value of the care and treatment provided.",-1),d=e("p",null,"1984, c. 42; 1985, c. 205.",-1),l=[i,n,d];function c(p,f,h,m,v,u){return t(),o("div",null,l)}const g=a(s,[["render",c]]);export{_ as __pageData,g as default};
