import{_ as r,o as a,c as n,z as e,a as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 8.01-379.3 General verdict accompanied by answer to interrogatories.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-379.3.md","filePath":"vacode/8.01-379.3.md"}'),o={name:"vacode/8.01-379.3.md"},i=e("h1",{id:"§-8-01-379-3-general-verdict-accompanied-by-answer-to-interrogatories",tabindex:"-1"},[t("§ 8.01-379.3 General verdict accompanied by answer to interrogatories. "),e("a",{class:"header-anchor",href:"#§-8-01-379-3-general-verdict-accompanied-by-answer-to-interrogatories","aria-label":'Permalink to "§ 8.01-379.3 General verdict accompanied by answer to interrogatories."'},"​")],-1),s=e("p",null,"Except in actions for negligence resulting in injury to person or death by wrongful act, in civil actions when the court determines that the complexity of the issues warrant, the court may submit to the jury, together with appropriate forms for a general verdict, written interrogatories upon one or more issues of fact the decision of which is necessary to a verdict. In actions for negligence resulting in injury to person or death by wrongful act, the court shall submit to the jury a general verdict form only, provided that the court may submit interrogatories to the jury if otherwise specifically authorized by law, if under substantive law governing the case comparative negligence applies, or if all parties to the action agree that interrogatories may be submitted to the jury. The court shall give such explanation or instruction as may be necessary to enable the jury both to make answers to the interrogatories and to render a general verdict, and the court shall direct the jury both to make written answers and to render a general verdict. When the general verdict and the answers are consistent, the appropriate judgment upon the verdict and answers shall be entered by the court. When the answers are consistent with each other but one or more is inconsistent with the general verdict, or when the answers are inconsistent with each other and one or more is likewise inconsistent with the general verdict, judgment shall not be entered and the court shall either order the jury to further consider its answers and verdict or order a new trial.",-1),c=e("p",null,[t("2005, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0499",target:"_blank",rel:"noreferrer"},"499"),t(".")],-1),h=[i,s,c];function d(l,u,g,p,m,w){return a(),n("div",null,h)}const y=r(o,[["render",d]]);export{v as __pageData,y as default};
