import{_ as t,o as i,c as o,z as e,a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 8.01-38.1 Limitation on recovery of punitive damages.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-38.1.md","filePath":"vacode/8.01-38.1.md"}'),n={name:"vacode/8.01-38.1.md"},r=e("h1",{id:"§-8-01-38-1-limitation-on-recovery-of-punitive-damages",tabindex:"-1"},[a("§ 8.01-38.1 Limitation on recovery of punitive damages. "),e("a",{class:"header-anchor",href:"#§-8-01-38-1-limitation-on-recovery-of-punitive-damages","aria-label":'Permalink to "§ 8.01-38.1 Limitation on recovery of punitive damages."'},"​")],-1),s=e("p",null,[a("In any action accruing on or after July 1, 1988, including an action for medical malpractice under Chapter 21.1 (§ "),e("a",{href:"http://law.lis.virginia.gov/vacode/8.01-581.1/",target:"_blank",rel:"noreferrer"},"8.01-581.1"),a(" et seq.), the total amount awarded for punitive damages against all defendants found to be liable shall be determined by the trier of fact. In no event shall the total amount awarded for punitive damages exceed $350,000. The jury shall not be advised of the limitation prescribed by this section. However, if a jury returns a verdict for punitive damages in excess of the maximum amount specified in this section, the judge shall reduce the award and enter judgment for such damages in the maximum amount provided by this section.")],-1),d=e("p",null,"1987, c. 255.",-1),c=[r,s,d];function l(m,u,h,f,p,v){return i(),o("div",null,c)}const y=t(n,[["render",l]]);export{g as __pageData,y as default};
