import{_ as e,o as i,c as t,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 8.01-390 Nonjudicial records as evidence (Subdivision (10)(a) of Supreme Court Rule 2:803 derived from subsection C of this section).","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-390.md","filePath":"vacode/8.01-390.md"}'),r={name:"vacode/8.01-390.md"},a=o('<h1 id="§-8-01-390-nonjudicial-records-as-evidence-subdivision-10-a-of-supreme-court-rule-2-803-derived-from-subsection-c-of-this-section" tabindex="-1">§ 8.01-390 Nonjudicial records as evidence (Subdivision (10)(a) of Supreme Court Rule 2:803 derived from subsection C of this section). <a class="header-anchor" href="#§-8-01-390-nonjudicial-records-as-evidence-subdivision-10-a-of-supreme-court-rule-2-803-derived-from-subsection-c-of-this-section" aria-label="Permalink to &quot;§ 8.01-390 Nonjudicial records as evidence (Subdivision (10)(a) of Supreme Court Rule 2:803 derived from subsection C of this section).&quot;">​</a></h1><p>A. Copies of records of this Commonwealth, of another state, of the United States, of another country, or of any political subdivision or agency of the same, other than those located in a clerk&#39;s office of a court, shall be received as prima facie evidence, provided that such copies are authenticated to be true copies either by the custodian thereof or by the person to whom the custodian reports, if they are different. A digitally certified copy of a record provided pursuant to the provisions of Chapter 38.2 (§ <a href="http://law.lis.virginia.gov/vacode/2.2-3817/" target="_blank" rel="noreferrer">2.2-3817</a> et seq.) of Title 2.2, whether in electronic form or in print form with visible assurance of the digital signature, shall be deemed to be authenticated by the custodian of the record unless evidence is presented to the contrary.</p><p>B. Records and recordings of 911 emergency service calls shall be deemed authentic transcriptions or recordings of the original statements if they are accompanied by a certificate that meets the provisions of subsection A and the certificate contains the date and time of the incoming call and the incoming phone number, if available, associated with the call.</p><p>C. An affidavit signed by an officer deemed to have custody of such an official record, or by his deputy, stating that after a diligent search, no record or entry of such record is found to exist among the records in his office is admissible as evidence that his office has no such record or entry.</p><p>1977, c. 617; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0668" target="_blank" rel="noreferrer">668</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0334" target="_blank" rel="noreferrer">334</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0353" target="_blank" rel="noreferrer">353</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0738" target="_blank" rel="noreferrer">738</a>.</p>',5),s=[a];function n(c,d,f,h,l,u){return i(),t("div",null,s)}const m=e(r,[["render",n]]);export{v as __pageData,m as default};
