import{_ as i,o as e,c as o,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 8.01-40.3 Unauthorized dissemination, etc., of criminal history record information; civil action.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-40.3.md","filePath":"vacode/8.01-40.3.md"}'),r={name:"vacode/8.01-40.3.md"},a=t('<h1 id="§-8-01-40-3-unauthorized-dissemination-etc-of-criminal-history-record-information-civil-action" tabindex="-1">§ 8.01-40.3 Unauthorized dissemination, etc., of criminal history record information; civil action. <a class="header-anchor" href="#§-8-01-40-3-unauthorized-dissemination-etc-of-criminal-history-record-information-civil-action" aria-label="Permalink to &quot;§ 8.01-40.3 Unauthorized dissemination, etc., of criminal history record information; civil action.&quot;">​</a></h1><p>A. Any person who disseminates, publishes, or maintains or causes to be disseminated, published, or maintained the criminal history record information as defined in § <a href="http://law.lis.virginia.gov/vacode/9.1-101/" target="_blank" rel="noreferrer">9.1-101</a> of an individual pertaining to that individual&#39;s charge or arrest for a criminal offense and solicits, requests, or accepts money or other thing of value for removing such criminal history record information shall be liable to the individual who is the subject of the information for actual damages or $500, whichever is greater, in addition to reasonable attorney fees and costs.</p><p>B. Nothing in this section shall be construed to impose liability on:</p><ol><li><p>An interactive computer service, as defined in 47 U.S.C. § 230(f), for content provided by another person.</p></li><li><p>Any speech protected by Article I, Section 12 of the Constitution of Virginia.</p></li></ol><p>C. As used in this section, &quot;criminal history record information&quot; means the same as that term is defined in § <a href="http://law.lis.virginia.gov/vacode/9.1-101/" target="_blank" rel="noreferrer">9.1-101</a>.</p><p>2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0414" target="_blank" rel="noreferrer">414</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0415" target="_blank" rel="noreferrer">415</a>.</p>',6),n=[a];function s(c,l,d,h,f,m){return e(),o("div",null,n)}const u=i(r,[["render",s]]);export{_ as __pageData,u as default};
