import{_ as e,o as a,c as o,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 8.01-40.4 Civil action for unlawful creation of image of another or unlawful dissemination or sale of images of another.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-40.4.md","filePath":"vacode/8.01-40.4.md"}'),r={name:"vacode/8.01-40.4.md"},t=i('<h1 id="§-8-01-40-4-civil-action-for-unlawful-creation-of-image-of-another-or-unlawful-dissemination-or-sale-of-images-of-another" tabindex="-1">§ 8.01-40.4 Civil action for unlawful creation of image of another or unlawful dissemination or sale of images of another. <a class="header-anchor" href="#§-8-01-40-4-civil-action-for-unlawful-creation-of-image-of-another-or-unlawful-dissemination-or-sale-of-images-of-another" aria-label="Permalink to &quot;§ 8.01-40.4 Civil action for unlawful creation of image of another or unlawful dissemination or sale of images of another.&quot;">​</a></h1><p>A. Any person injured by an individual who engaged in conduct that is prohibited under § <a href="http://law.lis.virginia.gov/vacode/18.2-386.1/" target="_blank" rel="noreferrer">18.2-386.1</a> or <a href="http://law.lis.virginia.gov/vacode/18.2-386.2/" target="_blank" rel="noreferrer">18.2-386.2</a>, whether or not the individual has been charged with or convicted of the alleged violation, may sue therefor and recover compensatory damages, punitive damages, and reasonable attorney fees and costs.</p><p>B. No action shall be commenced under this section more than two years after the later of (i) the date of the last act in violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-386.1/" target="_blank" rel="noreferrer">18.2-386.1</a> or <a href="http://law.lis.virginia.gov/vacode/18.2-386.2/" target="_blank" rel="noreferrer">18.2-386.2</a>, (ii) the date on which such person attained 18 years of age, or (iii) the date on which such person discovered or reasonably should have discovered the prohibited conduct.</p><p>C. Nothing in this section shall be construed to impose liability on an interactive computer service, as defined in 47 U.S.C. § 230(f), for content provided by another person.</p><p>2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0656" target="_blank" rel="noreferrer">656</a>.</p>',5),n=[t];function l(s,c,f,h,d,u){return a(),o("div",null,n)}const p=e(r,[["render",l]]);export{g as __pageData,p as default};
