import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 8.01-400.2 Communications between certain mental health professionals and clients (Supreme Court Rule 2:506 derived from this section).","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-400.2.md","filePath":"vacode/8.01-400.2.md"}'),r={name:"vacode/8.01-400.2.md"},a=n('<h1 id="§-8-01-400-2-communications-between-certain-mental-health-professionals-and-clients-supreme-court-rule-2-506-derived-from-this-section" tabindex="-1">§ 8.01-400.2 Communications between certain mental health professionals and clients (Supreme Court Rule 2:506 derived from this section). <a class="header-anchor" href="#§-8-01-400-2-communications-between-certain-mental-health-professionals-and-clients-supreme-court-rule-2-506-derived-from-this-section" aria-label="Permalink to &quot;§ 8.01-400.2 Communications between certain mental health professionals and clients (Supreme Court Rule 2:506 derived from this section).&quot;">​</a></h1><p>Except at the request of or with the consent of the client, no licensed professional counselor, as defined in § <a href="http://law.lis.virginia.gov/vacode/54.1-3500/" target="_blank" rel="noreferrer">54.1-3500</a>; licensed clinical social worker, as defined in § <a href="http://law.lis.virginia.gov/vacode/54.1-3700/" target="_blank" rel="noreferrer">54.1-3700</a>; licensed psychologist, as defined in § <a href="http://law.lis.virginia.gov/vacode/54.1-3600/" target="_blank" rel="noreferrer">54.1-3600</a>; or licensed marriage and family therapist, as defined in § <a href="http://law.lis.virginia.gov/vacode/54.1-3500/" target="_blank" rel="noreferrer">54.1-3500</a>, shall be required in giving testimony as a witness in any civil action to disclose any information communicated to him in a confidential manner, properly entrusted to him in his professional capacity and necessary to enable him to discharge his professional or occupational services according to the usual course of his practice or discipline, wherein such person so communicating such information about himself or another is seeking professional counseling or treatment and advice relative to and growing out of the information so imparted; provided, however, that when the physical or mental condition of the client is at issue in such action, or when a court, in the exercise of sound discretion, deems such disclosure necessary to the proper administration of justice, no fact communicated to, or otherwise learned by, such practitioner in connection with such counseling, treatment or advice shall be privileged, and disclosure may be required. The privileges conferred by this section shall not extend to testimony in matters relating to child abuse and neglect nor serve to relieve any person from the reporting requirements set forth in § <a href="http://law.lis.virginia.gov/vacode/63.2-1509/" target="_blank" rel="noreferrer">63.2-1509</a>.</p><p>1982, c. 537; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0110" target="_blank" rel="noreferrer">110</a>.</p>',3),o=[a];function s(c,l,d,h,m,u){return i(),t("div",null,o)}const v=e(r,[["render",s]]);export{p as __pageData,v as default};
