import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 8.01-412.10 Issuance of subpoena.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-412.10.md","filePath":"vacode/8.01-412.10.md"}'),n={name:"vacode/8.01-412.10.md"},i=a('<h1 id="§-8-01-412-10-issuance-of-subpoena" tabindex="-1">§ 8.01-412.10 Issuance of subpoena. <a class="header-anchor" href="#§-8-01-412-10-issuance-of-subpoena" aria-label="Permalink to &quot;§ 8.01-412.10 Issuance of subpoena.&quot;">​</a></h1><p>A. To request the issuance of a subpoena under this article, a party shall submit to the clerk of court in the circuit in which discovery is sought to be conducted in the Commonwealth (i) a foreign subpoena and (ii) a written statement that the law of the foreign jurisdiction grants reciprocal privileges to citizens of the Commonwealth for taking discovery in the jurisdiction that issued the foreign subpoena.</p><p>B. When a party submits a foreign subpoena to a clerk of court in the Commonwealth, the clerk, in accordance with that court&#39;s procedure, shall promptly issue a subpoena for service upon the person to which the foreign subpoena is directed.</p><p>C. A subpoena under subsection B shall:</p><ol><li><p>Incorporate the terms used in the foreign subpoena; and</p></li><li><p>Contain or be accompanied by the names, addresses, and telephone numbers of all counsel of record in the proceeding to which the subpoena relates and of any party not represented by counsel.</p></li></ol><p>D. A request for the issuance of a subpoena under this article does not constitute an appearance in the courts of the Commonwealth, and no civil action need be filed in the circuit court of the Commonwealth.</p><p>E. The provisions of this article shall be in addition to other procedures authorized in the Code of Virginia and the rules of court for obtaining discovery, except that no subpoena issued in the Commonwealth pursuant to this article may be issued by any person other than the applicable circuit court clerk of court in the Commonwealth, in accordance with subsections A and B.</p><p>2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0701" target="_blank" rel="noreferrer">701</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0530" target="_blank" rel="noreferrer">530</a>.</p>',8),r=[i];function s(c,h,u,p,l,d){return t(),o("div",null,r)}const m=e(n,[["render",s]]);export{b as __pageData,m as default};
