import{_ as o,o as n,c as i,z as e,a as t}from"./chunks/framework.9482e208.js";const x=JSON.parse('{"title":"§ 8.01-412.4 Procedure.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-412.4.md","filePath":"vacode/8.01-412.4.md"}'),a={name:"vacode/8.01-412.4.md"},s=e("h1",{id:"§-8-01-412-4-procedure",tabindex:"-1"},[t("§ 8.01-412.4 Procedure. "),e("a",{class:"header-anchor",href:"#§-8-01-412-4-procedure","aria-label":'Permalink to "§ 8.01-412.4 Procedure."'},"​")],-1),r=e("p",null,"The taking of audio-visual depositions shall be in accordance with the rules of the Supreme Court generally applicable to depositions. However, the following procedure shall be observed in recording an audio-visual deposition:",-1),c=e("p",null,"The deposition must begin with an oral or written statement on camera which includes (i) each operator's name and business address or, if applicable, the identity of the video conferencing or teleconferencing proprietor and locations participating in the video conference or teleconference; (ii) the name and business address of the operator's employer; (iii) the date, time and place of the deposition; (iv) the caption of the case; (v) the name of the witness; (vi) the party on whose behalf the deposition is being taken; (vii) with respect to video conferencing or teleconferencing, the identities of persons present at the deposition and the location of each such person; and (viii) any stipulations by the parties.",-1),d=e("p",null,"In addition, all counsel present on behalf of any party or witness shall identify themselves on camera. The oath for witnesses shall be administered on camera. If the length of a deposition requires the use of more than one recording unit, the end of each unit and the beginning of each succeeding unit shall be announced on camera. At the conclusion of a deposition, a statement shall be made on camera that the deposition is concluded. A statement may be made on camera setting forth any stipulations made by counsel concerning the custody of the audio-visual recording and exhibits or other pertinent matters.",-1),h=e("p",null,"All objections must be made as in the case of stenographic depositions. In any case where the court orders the audio-visual recording to be edited prior to its use, the original recording shall not be altered but shall be maintained as is.",-1),l=e("p",null,"Unless otherwise stipulated by the parties, the original audio-visual recording of a deposition, any copy edited pursuant to an order of the court, and exhibits shall be filed with the clerk of the court in accordance with the rules of the Supreme Court.",-1),p=e("p",null,[t("1983, c. 305; 1993, c. 208; 2000, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0821",target:"_blank",rel:"noreferrer"},"821"),t(".")],-1),u=[s,r,c,d,h,l,p];function f(m,b,g,_,v,y){return n(),i("div",null,u)}const k=o(a,[["render",f]]);export{x as __pageData,k as default};
