import{_ as e,o as i,c as a,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 8.01-42.1 Civil action for racial, religious, or ethnic harassment, violence or vandalism.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-42.1.md","filePath":"vacode/8.01-42.1.md"}'),t={name:"vacode/8.01-42.1.md"},r=o('<h1 id="§-8-01-42-1-civil-action-for-racial-religious-or-ethnic-harassment-violence-or-vandalism" tabindex="-1">§ 8.01-42.1 Civil action for racial, religious, or ethnic harassment, violence or vandalism. <a class="header-anchor" href="#§-8-01-42-1-civil-action-for-racial-religious-or-ethnic-harassment-violence-or-vandalism" aria-label="Permalink to &quot;§ 8.01-42.1 Civil action for racial, religious, or ethnic harassment, violence or vandalism.&quot;">​</a></h1><p>A. An action for injunctive relief or civil damages, or both, shall lie for any person who is subjected to acts of (i) intimidation or harassment, (ii) violence directed against his person, or (iii) vandalism directed against his real or personal property, where such acts are motivated by racial, religious, gender, disability, gender identity, sexual orientation, or ethnic animosity.</p><p>B. Any aggrieved party who initiates and prevails in an action authorized by this section shall be entitled to damages, including punitive damages, and in the discretion of the court to an award of the cost of the litigation and reasonable attorney fees in an amount to be fixed by the court.</p><p>C. The provisions of this section shall not apply to any actions between an employee and his employer, or between or among employees of the same employer, for damages arising out of incidents occurring in the workplace or arising out of the employee-employer relationship.</p><p>D. As used in this section:</p><p>&quot;Disability&quot; means a physical or mental impairment that substantially limits one or more of a person&#39;s major life activities.</p><p>1988, c. 492; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0746" target="_blank" rel="noreferrer">746</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1171" target="_blank" rel="noreferrer">1171</a>.</p>',7),n=[r];function s(l,c,h,d,p,m){return i(),a("div",null,n)}const g=e(t,[["render",s]]);export{v as __pageData,g as default};
