import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 8.01-42.3 Civil action for stalking.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-42.3.md","filePath":"vacode/8.01-42.3.md"}'),o={name:"vacode/8.01-42.3.md"},r=i('<h1 id="§-8-01-42-3-civil-action-for-stalking" tabindex="-1">§ 8.01-42.3 Civil action for stalking. <a class="header-anchor" href="#§-8-01-42-3-civil-action-for-stalking" aria-label="Permalink to &quot;§ 8.01-42.3 Civil action for stalking.&quot;">​</a></h1><p>A. A victim has a civil cause of action against an individual who engaged in conduct that is prohibited under § <a href="http://law.lis.virginia.gov/vacode/18.2-60.3/" target="_blank" rel="noreferrer">18.2-60.3</a>, whether or not the individual has been charged or convicted for the alleged violation, for the compensatory damages incurred by the victim as a result of that conduct, in addition to the costs for bringing the action. If compensatory damages are awarded, a victim may also be awarded punitive damages.</p><p>B. As used in this section:</p><p>&quot;Compensatory damages&quot; includes damages for all of the defendant&#39;s acts prohibited by § <a href="http://law.lis.virginia.gov/vacode/18.2-60.3/" target="_blank" rel="noreferrer">18.2-60.3</a>.</p><p>&quot;Victim&quot; means a person who, because of the conduct of the defendant that is prohibited under § <a href="http://law.lis.virginia.gov/vacode/18.2-60.3/" target="_blank" rel="noreferrer">18.2-60.3</a>, was placed in reasonable fear of death, criminal sexual assault, or bodily injury to himself or to a minor child of whom the person is a parent or legal guardian.</p><p>C. No action shall be commenced under this section more than two years after the most recent conduct prohibited under § <a href="http://law.lis.virginia.gov/vacode/18.2-60.3/" target="_blank" rel="noreferrer">18.2-60.3</a>.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0444" target="_blank" rel="noreferrer">444</a>.</p>',7),n=[r];function s(c,d,l,h,f,p){return a(),t("div",null,n)}const u=e(o,[["render",s]]);export{_ as __pageData,u as default};
