import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 8.01-42.5 Civil action for female genital mutilation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-42.5.md","filePath":"vacode/8.01-42.5.md"}'),o={name:"vacode/8.01-42.5.md"},n=i('<h1 id="§-8-01-42-5-civil-action-for-female-genital-mutilation" tabindex="-1">§ 8.01-42.5 Civil action for female genital mutilation. <a class="header-anchor" href="#§-8-01-42-5-civil-action-for-female-genital-mutilation" aria-label="Permalink to &quot;§ 8.01-42.5 Civil action for female genital mutilation.&quot;">​</a></h1><p>A. Any person injured by an individual who engaged in conduct that is prohibited under § <a href="http://law.lis.virginia.gov/vacode/18.2-51.7/" target="_blank" rel="noreferrer">18.2-51.7</a>, whether or not the individual has been charged with or convicted of the alleged violation, may sue therefor and recover compensatory damages, punitive damages, and reasonable attorney fees and costs.</p><p>B. No action shall be commenced under this section more than 10 years after the later of (i) the date of the last act in violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-51.7/" target="_blank" rel="noreferrer">18.2-51.7</a> or (ii) the date on which such person attained 18 years of age.</p><p>2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0667" target="_blank" rel="noreferrer">667</a>.</p>',4),r=[n];function l(c,s,d,h,f,_){return a(),t("div",null,r)}const v=e(o,[["render",l]]);export{p as __pageData,v as default};
