import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 8.01-420.2 Limitation on use of recorded conversations as evidence.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-420.2.md","filePath":"vacode/8.01-420.2.md"}'),n={name:"vacode/8.01-420.2.md"},a=i('<h1 id="§-8-01-420-2-limitation-on-use-of-recorded-conversations-as-evidence" tabindex="-1">§ 8.01-420.2 Limitation on use of recorded conversations as evidence. <a class="header-anchor" href="#§-8-01-420-2-limitation-on-use-of-recorded-conversations-as-evidence" aria-label="Permalink to &quot;§ 8.01-420.2 Limitation on use of recorded conversations as evidence.&quot;">​</a></h1><p>No mechanical recording, electronic or otherwise, of a telephone conversation shall be admitted into evidence in any civil proceeding unless (i) all parties to the conversation were aware the conversation was being recorded or (ii) the portion of the recording to be admitted contains admissions that, if true, would constitute criminal conduct which is the basis for the civil action, and one of the parties was aware of the recording and the proceeding is not one for divorce, separate maintenance or annulment of a marriage. The parties&#39; knowledge of the recording pursuant to clause (i) shall be demonstrated by a declaration at the beginning of the recorded portion of the conversation to be admitted into evidence that the conversation is being recorded. This section shall not apply to emergency reporting systems operated by police and fire departments and by emergency medical services agencies, nor to any communications common carrier utilizing service observing or random monitoring pursuant to § <a href="http://law.lis.virginia.gov/vacode/19.2-62/" target="_blank" rel="noreferrer">19.2-62</a>.</p><p>1983, c. 503; 1992, c. 567; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>.</p>',3),r=[a];function c(s,d,l,h,p,m){return o(),t("div",null,r)}const g=e(n,[["render",c]]);export{f as __pageData,g as default};
