import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 8.01-420.8 Protection of confidential information in court files.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-420.8.md","filePath":"vacode/8.01-420.8.md"}'),r={name:"vacode/8.01-420.8.md"},a=o('<h1 id="§-8-01-420-8-protection-of-confidential-information-in-court-files" tabindex="-1">§ 8.01-420.8 Protection of confidential information in court files. <a class="header-anchor" href="#§-8-01-420-8-protection-of-confidential-information-in-court-files" aria-label="Permalink to &quot;§ 8.01-420.8 Protection of confidential information in court files.&quot;">​</a></h1><p>A. Whenever a party files, or causes to be filed, with the court a motion, pleading, subpoena, exhibit, or other document containing a social security number or other identification number appearing on a driver&#39;s license or other document issued under Chapter 3 (§ <a href="/vacode/46.2-300/">46.2-300</a> et seq.) of Title 46.2 or the comparable law of another jurisdiction, or on a credit card, debit card, bank account, or other electronic billing and payment system, the party shall make reasonable efforts to redact all but the last four digits of the identification number.</p><p>B. The provisions of subsection A apply to all civil actions in circuit and district court, unless there is a specific statute to the contrary that applies to the particular type of proceeding in which the party is involved.</p><p>C. Nothing in this section shall create a private cause of action against the party or lawyer who filed the document or any court personnel, the clerk, or any employees of the clerk&#39;s office who received it for filing.</p><p>2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0427" target="_blank" rel="noreferrer">427</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1227" target="_blank" rel="noreferrer">1227</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1246" target="_blank" rel="noreferrer">1246</a>.</p>',5),n=[a];function c(l,s,f,h,p,d){return t(),i("div",null,n)}const m=e(r,[["render",c]]);export{_ as __pageData,m as default};
