import{_ as t,o as r,c as o,z as e,a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 8.01-429 Action of appellate court when there might be redress under § 8.01-428.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-429.md","filePath":"vacode/8.01-429.md"}'),n={name:"vacode/8.01-429.md"},s=e("h1",{id:"§-8-01-429-action-of-appellate-court-when-there-might-be-redress-under-§-8-01-428",tabindex:"-1"},[a("§ 8.01-429 Action of appellate court when there might be redress under § 8.01-428. "),e("a",{class:"header-anchor",href:"#§-8-01-429-action-of-appellate-court-when-there-might-be-redress-under-§-8-01-428","aria-label":'Permalink to "§ 8.01-429 Action of appellate court when there might be redress under § 8.01-428."'},"​")],-1),h=e("p",null,"No appeal shall be allowed by the Court of Appeals or the Supreme Court or any judge or justice thereof for any matter for which a judgment or decree is liable to be reversed or amended, on motion as aforesaid, by the court which rendered it, or the judge thereof, until such motion is made and overruled in whole or in part. And when the Court of Appeals or the Supreme Court hears a case on appeal, if it appears that, either before or since the appeal, the judgment or decree has been so amended, the Court of Appeals or the Supreme Court shall affirm the judgment or decree, unless there is other error. If it appears that the amendment ought to be, and has not been made, the Court of Appeals or the Supreme Court may make such amendment, and affirm in like manner the judgment or decree, unless there is other error.",-1),d=e("p",null,"Code 1950, § 8-349; 1977, c. 617; 1984, c. 703.",-1),i=[s,h,d];function c(l,p,u,m,f,_){return r(),o("div",null,i)}const w=t(n,[["render",c]]);export{g as __pageData,w as default};
