import{_ as o,o as t,c as n,z as e,a as s}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 8.01-432 Confession of judgment irrespective of suit pending.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-432.md","filePath":"vacode/8.01-432.md"}'),i={name:"vacode/8.01-432.md"},r=e("h1",{id:"§-8-01-432-confession-of-judgment-irrespective-of-suit-pending",tabindex:"-1"},[s("§ 8.01-432 Confession of judgment irrespective of suit pending. "),e("a",{class:"header-anchor",href:"#§-8-01-432-confession-of-judgment-irrespective-of-suit-pending","aria-label":'Permalink to "§ 8.01-432 Confession of judgment irrespective of suit pending."'},"​")],-1),a=e("p",null,"Any person being indebted to another person, or any attorney-in-fact pursuant to a power of attorney, may at any time confess judgment in the clerk's office of any circuit court in this Commonwealth, whether a suit, motion or action be pending therefor or not, for only such principal and interest as his creditor may be willing to accept a judgment for, which judgment, when so confessed, shall be forthwith entered of record by the clerk in whose office it is confessed, in the proper order book of his court. Such judgment shall be as final and as binding as though confessed in open court or rendered by the court, subject to the control of the court in the clerk's office of which the same shall have been confessed.",-1),c=e("p",null,"Code 1950, § 8-356; 1977, c. 617.",-1),d=[r,a,c];function f(h,p,u,l,m,_){return t(),n("div",null,d)}const v=o(i,[["render",f]]);export{b as __pageData,v as default};
