import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 8.01-452.1 Disposal of exhibits in civil cases.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-452.1.md","filePath":"vacode/8.01-452.1.md"}'),s={name:"vacode/8.01-452.1.md"},o=t('<h1 id="§-8-01-452-1-disposal-of-exhibits-in-civil-cases" tabindex="-1">§ 8.01-452.1 Disposal of exhibits in civil cases. <a class="header-anchor" href="#§-8-01-452-1-disposal-of-exhibits-in-civil-cases" aria-label="Permalink to &quot;§ 8.01-452.1 Disposal of exhibits in civil cases.&quot;">​</a></h1><p>A clerk of court, after sixty days have elapsed from the entry of judgment in a civil case or, if the civil case is appealed or notice of appeal is pending or the case is being reheard, when the appeal or rehearing is concluded, may dispose of or donate any exhibits filed in the case and in his possession after notifying the owner or his attorney by first-class mail and after twenty-one days from the mailing of the notice to the owner or attorney unless the owner or attorney requests the return of the exhibits.</p><p>1981, c. 312; 1992, c. 57; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0013" target="_blank" rel="noreferrer">13</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0135" target="_blank" rel="noreferrer">135</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0886" target="_blank" rel="noreferrer">886</a>.</p>',3),r=[o];function n(c,l,h,f,p,d){return i(),a("div",null,r)}const v=e(s,[["render",n]]);export{g as __pageData,v as default};
