import{_ as t,o,c as n,z as e,a}from"./chunks/framework.9482e208.js";const j=JSON.parse('{"title":"§ 8.01-456 Satisfaction of judgment when judgment creditor cannot be located.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-456.md","filePath":"vacode/8.01-456.md"}'),r={name:"vacode/8.01-456.md"},i=e("h1",{id:"§-8-01-456-satisfaction-of-judgment-when-judgment-creditor-cannot-be-located",tabindex:"-1"},[a("§ 8.01-456 Satisfaction of judgment when judgment creditor cannot be located. "),e("a",{class:"header-anchor",href:"#§-8-01-456-satisfaction-of-judgment-when-judgment-creditor-cannot-be-located","aria-label":'Permalink to "§ 8.01-456 Satisfaction of judgment when judgment creditor cannot be located."'},"​")],-1),d=e("p",null,"Whenever a judgment debtor or anyone for him or any party liable on the judgment wishes to pay off and discharge a judgment, of record in any clerk's office in this Commonwealth, when the judgment creditor cannot be located, he may do so by paying into the court having jurisdiction over such judgment an amount sufficient to pay the principal, interest, and all costs due thereupon, together with the cost of entering necessary orders, and other service attendant upon the proceeding herein provided for, and satisfaction upon such judgment. Upon such payment, the court, by an order entered of record shall direct the clerk to deposit the same at interest in any bank which is a member of the Federal Deposit Insurance Corporation and is designated in such order; to file evidence of such deposit in the office of the clerk in an appropriate file and shall be payable to the court entering the order for the benefit of the judgment creditor; and to enter upon the judgment docket, where the judgment is docketed, the date of such deposit, the date of the entry of the order of the court receiving same, referring to the number and page of the order book in which it is entered.",-1),c=e("p",null,"The judgment creditor or his attorney may have the money, so paid, to which he is entitled, upon application to the court therefor whenever it may appear to the court that it should be paid to him.",-1),h=e("p",null,"From and after the time of such payment, into the court, as aforesaid, the property of the defendant shall be free and clear of any lien created by any such judgment, or any execution issued thereupon.",-1),s=e("p",null,"Code 1950, § 8-384; 1977, c. 617.",-1),u=[i,d,c,h,s];function f(l,p,m,g,y,_){return o(),n("div",null,u)}const v=t(r,[["render",f]]);export{j as __pageData,v as default};
