import{_ as t,o as a,c as o,z as e,a as s}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 8.01-464 Order of liability between alienees of different parts of estate.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-464.md","filePath":"vacode/8.01-464.md"}'),i={name:"vacode/8.01-464.md"},n=e("h1",{id:"§-8-01-464-order-of-liability-between-alienees-of-different-parts-of-estate",tabindex:"-1"},[s("§ 8.01-464 Order of liability between alienees of different parts of estate. "),e("a",{class:"header-anchor",href:"#§-8-01-464-order-of-liability-between-alienees-of-different-parts-of-estate","aria-label":'Permalink to "§ 8.01-464 Order of liability between alienees of different parts of estate."'},"​")],-1),r=e("p",null,"When the real estate liable to the lien of a judgment is more than sufficient to satisfy the same, and it, or any part of it, has been aliened, as among the alienees for value, that which was aliened last, shall, in equity, be first liable, and so on with other successive alienations, until the whole judgment is satisfied. And as among alienees who are volunteers under such judgment debtor, the same rule as to the order of liability shall prevail; but as among alienees for value and volunteers, the lands aliened to the latter shall be subjected before the lands aliened to the former are resorted to; and, in either case, any part of such real estate retained by the debtor shall be first liable to the satisfaction of the judgment. An alienee for value, however, from a volunteer shall occupy the same position that he would have occupied had he purchased from the debtor at the time he purchased from the voluntary donee.",-1),l=e("p",null,"Code 1950, § 8-395; 1977, c. 617.",-1),d=[n,r,l];function h(f,c,u,b,m,p){return a(),o("div",null,d)}const y=t(i,[["render",h]]);export{v as __pageData,y as default};
