import{_ as e,o as t,c as n,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 8.01-465.13:6 Effect of recognition of foreign-country judgment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-465.13:6.md","filePath":"vacode/8.01-465.13:6.md"}'),r={name:"vacode/8.01-465.13:6.md"},a=o('<h1 id="§-8-01-465-13-6-effect-of-recognition-of-foreign-country-judgment" tabindex="-1">§ 8.01-465.13:6 Effect of recognition of foreign-country judgment. <a class="header-anchor" href="#§-8-01-465-13-6-effect-of-recognition-of-foreign-country-judgment" aria-label="Permalink to &quot;§ 8.01-465.13:6 Effect of recognition of foreign-country judgment.&quot;">​</a></h1><p>If the court in a proceeding under § <a href="http://law.lis.virginia.gov/vacode/8.01-465.13:5/" target="_blank" rel="noreferrer">8.01-465.13:5</a> finds that the foreign-country judgment is entitled to recognition under this chapter then, to the extent that the foreign-country judgment grants or denies recovery of a sum of money, the foreign-country judgment is:</p><ol><li><p>Conclusive between the parties to the same extent as the judgment of a sister state entitled to full faith and credit in the Commonwealth would be conclusive; and</p></li><li><p>Enforceable in the same manner and to the same extent as a judgment rendered in the Commonwealth.</p></li></ol><p>2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0462" target="_blank" rel="noreferrer">462</a>.</p>',4),i=[a];function c(f,d,s,l,g,h){return t(),n("div",null,i)}const m=e(r,[["render",c]]);export{_ as __pageData,m as default};
