import{_ as t,o,c as a,z as e,a as n}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 8.01-477 When executions may be quashed; how proceedings thereon stayed.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-477.md","filePath":"vacode/8.01-477.md"}'),s={name:"vacode/8.01-477.md"},h=e("h1",{id:"§-8-01-477-when-executions-may-be-quashed-how-proceedings-thereon-stayed",tabindex:"-1"},[n("§ 8.01-477 When executions may be quashed; how proceedings thereon stayed. "),e("a",{class:"header-anchor",href:"#§-8-01-477-when-executions-may-be-quashed-how-proceedings-thereon-stayed","aria-label":'Permalink to "§ 8.01-477 When executions may be quashed; how proceedings thereon stayed."'},"​")],-1),i=e("p",null,"A motion to quash an execution may, after reasonable notice to the adverse party, be heard and decided by the court which issued the execution. Such court, on the application of the plaintiff in the motion, may make an order staying the proceedings on the execution until the motion be heard and determined, the order not to be effectual until bond be given in such penalty and with such condition, and either with or without surety, as the court may prescribe. The clerk from whose office the execution issued, shall take the bond and make as many copies of the order as may be necessary and endorse thereon that the bond required has been given; and a copy shall be served on the plaintiff in the execution and on the officer in whose hands the execution is placed.",-1),d=e("p",null,"Code 1950, § 8-410; 1977, c. 617.",-1),r=[h,i,d];function c(u,l,p,m,f,y){return o(),a("div",null,r)}const x=t(s,[["render",c]]);export{b as __pageData,x as default};
