import{_ as t,o as n,c as i,z as e,a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 8.01-48 Mitigation in actions against newspapers, etc.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-48.md","filePath":"vacode/8.01-48.md"}'),o={name:"vacode/8.01-48.md"},r=e("h1",{id:"§-8-01-48-mitigation-in-actions-against-newspapers-etc",tabindex:"-1"},[a("§ 8.01-48 Mitigation in actions against newspapers, etc. "),e("a",{class:"header-anchor",href:"#§-8-01-48-mitigation-in-actions-against-newspapers-etc","aria-label":'Permalink to "§ 8.01-48 Mitigation in actions against newspapers, etc."'},"​")],-1),s=e("p",null,[a("In any civil action against the publisher, owner, editor, reporter or employee of any newspaper, magazine or periodical under § "),e("a",{href:"http://law.lis.virginia.gov/vacode/8.01-45/",target:"_blank",rel:"noreferrer"},"8.01-45"),a(", or for libel or defamation, because of any article, statement or other matter contained in any such newspaper, magazine or periodical, the defendant, whether punitive damages be sought or not, may introduce in evidence in mitigation of general and punitive damages, or either, but not of actual pecuniary damages, all the circumstances of the publication, including the source of the information, its character as affording reasonable ground of reliance, any prior publication elsewhere of similar purport, the lack of negligence or malice on the part of the defendant, the good faith of the defendant in such publication, or that apology or retraction, if any, was made with reasonable promptness and fairness; provided that the defendant may introduce in evidence only such circumstances and to the extent set forth in his or its grounds of defense.")],-1),c=e("p",null,"Code 1950, § 8-632; 1954, c. 333; 1977, c. 617.",-1),d=[r,s,c];function l(p,h,f,g,m,u){return n(),i("div",null,d)}const b=t(o,[["render",l]]);export{v as __pageData,b as default};
