import{_ as o,o as r,c as i,z as e,a as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 8.01-525.1 Recordation; notice of sale; preferences prohibited.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-525.1.md","filePath":"vacode/8.01-525.1.md"}'),a={name:"vacode/8.01-525.1.md"},n=e("h1",{id:"§-8-01-525-1-recordation-notice-of-sale-preferences-prohibited",tabindex:"-1"},[t("§ 8.01-525.1 Recordation; notice of sale; preferences prohibited. "),e("a",{class:"header-anchor",href:"#§-8-01-525-1-recordation-notice-of-sale-preferences-prohibited","aria-label":'Permalink to "§ 8.01-525.1 Recordation; notice of sale; preferences prohibited."'},"​")],-1),s=e("p",null,"Whenever a deed of assignment for the benefit of creditors is executed, the deed shall be recorded. If no notice of the sale has previously been given, the trustee named in such deed, or the one substituted in the manner prescribed in this article, before selling under the deed of assignment, shall, at least 10 days before the sale, notify each of the creditors named in the deed by certified mail, return receipt requested, advising of (i) the execution of such sale; (ii) when, where, and how the sale will be held; (iii) the terms of such sale; and (iv) whether or not the deed provides that acceptance shall be in full satisfaction. No creditor shall be preferred in the deed except those given a lien or preference by law, or those having a valid lien upon the property conveyed, or some part of such lien, and those having a lien shall be preferred only to the extent of the value of the property upon which they have a lien.",-1),d=e("p",null,[t("1924, p. 657; Michie Code 1942, § 5278b; Code 1950, § 55-156; 2019, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712",target:"_blank",rel:"noreferrer"},"712"),t(".")],-1),c=[n,s,d];function h(l,f,p,u,b,_){return r(),i("div",null,c)}const g=o(a,[["render",h]]);export{m as __pageData,g as default}