import{_ as t,o,c as n,z as e,a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 8.01-562 Examination on oath of codefendant; order and bond.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-562.md","filePath":"vacode/8.01-562.md"}'),r={name:"vacode/8.01-562.md"},i=e("h1",{id:"§-8-01-562-examination-on-oath-of-codefendant-order-and-bond",tabindex:"-1"},[a("§ 8.01-562 Examination on oath of codefendant; order and bond. "),e("a",{class:"header-anchor",href:"#§-8-01-562-examination-on-oath-of-codefendant-order-and-bond","aria-label":'Permalink to "§ 8.01-562 Examination on oath of codefendant; order and bond."'},"​")],-1),s=e("p",null,"A defendant who at the time of service of the attachment was alleged to be indebted to a principal defendant, or had in his possession personal property belonging to such principal defendant, shall appear in person and submit to an examination on oath touching such debt or personal property, or he may, with the consent of the court, after reasonable notice to the plaintiff, file an answer in writing under oath, stating whether or not he was so indebted, and if so, the amount thereof and the time of maturity, or whether he had in his possession any personal property belonging to such principal defendant and, if so, the nature and value thereof. If it appear on such examination or by his answer that at the time of the service of the attachment, he was indebted to the principal defendant, or had in his possession or control any goods, chattels, money, securities or other effects belonging to such defendant, the court may order him to pay the amount so owing by him, or to deliver such effects to the sheriff, or other person designated by the court to receive the same; or such defendant may, with the leave of the court, give bond with sufficient security, payable to such person and in such penalty as the court shall prescribe, with condition to pay the amount owing by him, and have such effects forthcoming, at such time and place as the court may thereafter require. An answer under oath under this section shall be deemed prima facie to be true.",-1),d=e("p",null,"Code 1950, § 8-550; 1977, c. 617.",-1),h=[i,s,d];function c(f,p,l,m,u,b){return o(),n("div",null,h)}const g=t(r,[["render",c]]);export{y as __pageData,g as default};
