import{_ as t,o as e,c as a,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 8.01-581.011 Modification or correction of award.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-581.011.md","filePath":"vacode/8.01-581.011.md"}'),r={name:"vacode/8.01-581.011.md"},i=o('<h1 id="§-8-01-581-011-modification-or-correction-of-award" tabindex="-1">§ 8.01-581.011 Modification or correction of award. <a class="header-anchor" href="#§-8-01-581-011-modification-or-correction-of-award" aria-label="Permalink to &quot;§ 8.01-581.011 Modification or correction of award.&quot;">​</a></h1><p>Upon application made within ninety days after delivery of a copy of the award to the applicant, the court shall modify or correct the award where:</p><ol><li><p>There was an evident miscalculation of figures or an evident mistake in the description of any person, thing or property referred to in the award;</p></li><li><p>The arbitrators have awarded upon a matter not submitted to them and the award may be corrected without affecting the merits of the decision upon the issues submitted; or</p></li><li><p>The award is imperfect in a matter of form, not affecting the merits of the controversy.</p></li></ol><p>If the application is granted, the court shall modify and correct the award so as to effect its intent and shall confirm the award as so modified and corrected. Otherwise, the court shall confirm the award as made.</p><p>An application to modify or correct an award may be joined in the alternative with an application to vacate the award.</p><p>1986, c. 614.</p>',6),n=[i];function c(d,s,h,f,p,l){return e(),a("div",null,n)}const w=t(r,[["render",c]]);export{_ as __pageData,w as default};
