import{_ as a,o as e,c as r,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 8.01-581.016 Appeals.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-581.016.md","filePath":"vacode/8.01-581.016.md"}'),i={name:"vacode/8.01-581.016.md"},n=t('<h1 id="§-8-01-581-016-appeals" tabindex="-1">§ 8.01-581.016 Appeals. <a class="header-anchor" href="#§-8-01-581-016-appeals" aria-label="Permalink to &quot;§ 8.01-581.016 Appeals.&quot;">​</a></h1><p>An appeal may be taken from:</p><ol><li><p>An order denying an application to compel arbitration made under § <a href="http://law.lis.virginia.gov/vacode/8.01-581.02/" target="_blank" rel="noreferrer">8.01-581.02</a>;</p></li><li><p>An order by a general district court granting an application to compel arbitration;</p></li><li><p>An order granting an application to stay arbitration made under subsection B of § <a href="http://law.lis.virginia.gov/vacode/8.01-581.02/" target="_blank" rel="noreferrer">8.01-581.02</a>;</p></li><li><p>An order confirming or denying an award;</p></li><li><p>An order modifying or correcting an award;</p></li><li><p>An order vacating an award without directing a rehearing; or</p></li><li><p>A judgment or decree entered pursuant to the provisions of this article.</p></li></ol><p>The appeal shall be taken in the manner and to the same extent as from orders or judgments in a civil action.</p><p>1986, c. 614; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0181" target="_blank" rel="noreferrer">181</a>.</p>',5),o=[n];function l(p,c,d,s,_,g){return e(),r("div",null,o)}const f=a(i,[["render",l]]);export{m as __pageData,f as default};
