import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 8.01-581.18 Delivery of results of laboratory tests and other examinations not authorized by physician.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-581.18.md","filePath":"vacode/8.01-581.18.md"}'),r={name:"vacode/8.01-581.18.md"},i=a('<h1 id="§-8-01-581-18-delivery-of-results-of-laboratory-tests-and-other-examinations-not-authorized-by-physician" tabindex="-1">§ 8.01-581.18 Delivery of results of laboratory tests and other examinations not authorized by physician. <a class="header-anchor" href="#§-8-01-581-18-delivery-of-results-of-laboratory-tests-and-other-examinations-not-authorized-by-physician" aria-label="Permalink to &quot;§ 8.01-581.18 Delivery of results of laboratory tests and other examinations not authorized by physician.&quot;">​</a></h1><p>A. Whenever a laboratory test or other examination of the physical or mental condition of any person is conducted by or under the supervision of a person other than a physician and not at the request or with the authorization of a physician, any report of the results of such test or examination shall be provided by the person conducting such test or examination to the person who was the subject of such test or examination. Such report shall state in bold type that it is the responsibility of the person so examined or tested to arrange with his physician for consultation and interpretation of the results of such test or examination. The provisions of this subsection shall not apply to any test or examination conducted under the auspices of the State Department of Health.</p><p>B. As used in this section and § <a href="http://law.lis.virginia.gov/vacode/8.01-581.18:1/" target="_blank" rel="noreferrer">8.01-581.18:1</a>, &quot;physician&quot; means a person licensed to practice medicine, podiatry, chiropractic or osteopathy in this Commonwealth pursuant to Chapter 29 (§ <a href="http://law.lis.virginia.gov/vacode/54.1-2900/" target="_blank" rel="noreferrer">54.1-2900</a> et seq.) of Title 54.1.</p><p>Code 1950, § 8-654.11; 1977, c. 527; 1993, c. 702; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0684" target="_blank" rel="noreferrer">684</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0877" target="_blank" rel="noreferrer">877</a>.</p>',4),n=[i];function s(h,c,l,p,d,f){return t(),o("div",null,n)}const _=e(r,[["render",s]]);export{y as __pageData,_ as default};
