import{_ as i,o as e,c as o,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 8.01-581.19 Civil immunity for physicians, psychologists, podiatrists, optometrists, veterinarians, nursing home administrators, and certified emergency medical services providers while members of certain committees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-581.19.md","filePath":"vacode/8.01-581.19.md"}'),t={name:"vacode/8.01-581.19.md"},r=n('<h1 id="§-8-01-581-19-civil-immunity-for-physicians-psychologists-podiatrists-optometrists-veterinarians-nursing-home-administrators-and-certified-emergency-medical-services-providers-while-members-of-certain-committees" tabindex="-1">§ 8.01-581.19 Civil immunity for physicians, psychologists, podiatrists, optometrists, veterinarians, nursing home administrators, and certified emergency medical services providers while members of certain committees. <a class="header-anchor" href="#§-8-01-581-19-civil-immunity-for-physicians-psychologists-podiatrists-optometrists-veterinarians-nursing-home-administrators-and-certified-emergency-medical-services-providers-while-members-of-certain-committees" aria-label="Permalink to &quot;§ 8.01-581.19 Civil immunity for physicians, psychologists, podiatrists, optometrists, veterinarians, nursing home administrators, and certified emergency medical services providers while members of certain committees.&quot;">​</a></h1><p>A. Any physician, chiropractor, psychologist, podiatrist, veterinarian, or optometrist licensed to practice in the Commonwealth shall be immune from civil liability for any communication, finding, opinion, or conclusion made in performance of his duties while serving as a member of any committee, board, group, commission, or other entity that is responsible for resolving questions concerning the admission of any physician, psychologist, podiatrist, veterinarian, or optometrist to, or the taking of disciplinary action against any member of, any medical society, academy, or association affiliated with the American Medical Association, the Virginia Academy of Clinical Psychologists, the American Psychological Association, the Virginia Applied Psychology Academy, the Virginia Academy of School Psychologists, the American Podiatric Medical Association, the American Veterinary Medical Association, the International Chiropractic Association, the American Chiropractic Association, the Virginia Chiropractic Association, or the American Optometric Association, provided that such communication, finding, opinion, or conclusion is not made in bad faith or with malicious intent.</p><p>B. Any nursing home administrator licensed under the laws of the Commonwealth shall be immune from civil liability for any communication, finding, opinion, decision, or conclusion made in performance of his duties while serving as a member of any committee, board, group, commission, or other entity that is responsible for resolving questions concerning the admission of any health care facility to, or the taking of disciplinary action against any member of, the Virginia Health Care Association, provided that such communication, finding, opinion, decision, or conclusion is not made in bad faith or with malicious intent.</p><p>C. Any emergency medical services provider who holds a valid certificate issued by the Commissioner of Health shall be immune from civil liability for any communication, finding, opinion, decision, or conclusion made in performance of his duties while serving as a member of any regional council, committee, board, group, commission, or other entity that is responsible for resolving questions concerning the quality of care, including triage, interfacility transfer, and other components of emergency medical services care, unless such communication, finding, opinion, decision, or conclusion is made in bad faith or with malicious intent.</p><p>1978, c. 541; 1987, c. 713; 1989, c. 729; 1993, c. 702; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0937" target="_blank" rel="noreferrer">937</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0980" target="_blank" rel="noreferrer">980</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0412" target="_blank" rel="noreferrer">412</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>.</p>',5),a=[r];function s(c,m,l,h,d,p){return e(),o("div",null,a)}const y=i(t,[["render",s]]);export{g as __pageData,y as default};
