import{_ as n,o as t,c as i,z as e,a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 8.01-6.1 Amendment of pleading changing or adding a claim or defense; relation back.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-6.1.md","filePath":"vacode/8.01-6.1.md"}'),o={name:"vacode/8.01-6.1.md"},r=e("h1",{id:"§-8-01-6-1-amendment-of-pleading-changing-or-adding-a-claim-or-defense-relation-back",tabindex:"-1"},[a("§ 8.01-6.1 Amendment of pleading changing or adding a claim or defense; relation back. "),e("a",{class:"header-anchor",href:"#§-8-01-6-1-amendment-of-pleading-changing-or-adding-a-claim-or-defense-relation-back","aria-label":'Permalink to "§ 8.01-6.1 Amendment of pleading changing or adding a claim or defense; relation back."'},"​")],-1),d=e("p",null,"Subject to any other applicable provisions of law, an amendment of a pleading changing or adding a claim or defense against a party relates back to the date of the original pleadings for purposes of the statute of limitations if the court finds (i) the claim or defense asserted in the amended pleading arose out of the conduct, transaction or occurrence set forth in the original pleading, (ii) the amending party was reasonably diligent in asserting the amended claim or defense, and (iii) parties opposing the amendment will not be substantially prejudiced in litigating on the merits as a result of the timing of the amendment. In connection with such an amendment, the trial court may grant a continuance or other relief to protect the parties. This section shall not apply to eminent domain or mechanics' lien claims or defenses.",-1),s=e("p",null,[a("1996, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0693",target:"_blank",rel:"noreferrer"},"693"),a(".")],-1),c=[r,d,s];function l(m,g,h,f,p,_){return t(),i("div",null,c)}const k=n(o,[["render",l]]);export{b as __pageData,k as default};
