import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 8.01-607 Appointment and removal.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-607.md","filePath":"vacode/8.01-607.md"}'),n={name:"vacode/8.01-607.md"},i=t('<h1 id="§-8-01-607-appointment-and-removal" tabindex="-1">§ 8.01-607 Appointment and removal. <a class="header-anchor" href="#§-8-01-607-appointment-and-removal" aria-label="Permalink to &quot;§ 8.01-607 Appointment and removal.&quot;">​</a></h1><p>A. Each circuit court may, from time to time, appoint such commissioners in chancery as may be deemed necessary for the convenient dispatch of the business of such court. Such commissioners shall be removable at pleasure.</p><p>B. Commissioners in chancery may be appointed in cases in circuit court, including uncontested divorce cases, only when:</p><ol><li><p>There is agreement by the parties with the concurrence of the court; or</p></li><li><p>Upon (i) motion of a party, or (ii) upon motion of the court, sua sponte. The court shall make a finding of good cause shown in each individual case.</p></li></ol><p>Code 1950, § 8-248; 1977, c. 617; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0885" target="_blank" rel="noreferrer">885</a>.</p>',5),r=[i];function c(s,p,l,m,d,h){return o(),a("div",null,r)}const f=e(n,[["render",c]]);export{u as __pageData,f as default};
