import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 8.01-66.5 Written notice required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-66.5.md","filePath":"vacode/8.01-66.5.md"}'),a={name:"vacode/8.01-66.5.md"},n=i('<h1 id="§-8-01-66-5-written-notice-required" tabindex="-1">§ 8.01-66.5 Written notice required. <a class="header-anchor" href="#§-8-01-66-5-written-notice-required" aria-label="Permalink to &quot;§ 8.01-66.5 Written notice required.&quot;">​</a></h1><p>A. No lien provided for in § <a href="http://law.lis.virginia.gov/vacode/8.01-66.2/" target="_blank" rel="noreferrer">8.01-66.2</a>, <a href="http://law.lis.virginia.gov/vacode/8.01-66.9/" target="_blank" rel="noreferrer">8.01-66.9</a>, or <a href="http://law.lis.virginia.gov/vacode/19.2-368.15/" target="_blank" rel="noreferrer">19.2-368.15</a> shall be created or become effective in favor of the Commonwealth, an institution thereof, or a hospital, nursing home, physician, nurse, or physical therapist, or emergency medical services and transportation provided by an emergency medical services vehicle, unless and until a written notice of lien setting forth the name of the Commonwealth or the institution, hospital, nursing home, physician, nurse, physical therapist, or emergency medical services agency that provided emergency medical services or emergency medical services vehicle transportation and the name of the injured person has been served upon or given to the person, firm, or corporation whose negligence is alleged to have caused such injuries, or to the attorney for the injured party, or to the injured party. Such written notice of lien shall not be required if the attorney for the injured party knew that medical services were either provided or paid for by the Commonwealth.</p><p>B. In any action for personal injuries or wrongful death against a nursing home or its agents, if the Department of Medical Assistance Services has paid for any health care services provided to the injured party or decedent relating to the action, the injured party or personal representative shall, within 60 days of filing a lawsuit or 21 days of determining that the Department of Medical Assistance Services has paid for such health care services, whichever is later, give written notice to the Department of Medical Assistance Services that the lawsuit has been filed. The Department of Medical Assistance Services shall provide a written response, stating the amount of the lien as of the date of their response, within 60 days of receiving a request for that information from the injured party or personal representative.</p><p>Code 1950, § 32-142; 1979, c. 722; 1980, c. 623; 1983, c. 263; 1988, c. 544; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0183" target="_blank" rel="noreferrer">183</a>; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0455" target="_blank" rel="noreferrer">455</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0525" target="_blank" rel="noreferrer">525</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0273" target="_blank" rel="noreferrer">273</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>.</p>',4),o=[n];function s(c,l,h,d,p,f){return r(),t("div",null,o)}const u=e(a,[["render",s]]);export{v as __pageData,u as default};
