import{_ as o,o as i,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 8.01-81 Who may compel partition of land; jurisdiction; validation of certain partitions of mineral rights; when shares of two or more laid off together.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-81.md","filePath":"vacode/8.01-81.md"}'),r={name:"vacode/8.01-81.md"},n=e("h1",{id:"§-8-01-81-who-may-compel-partition-of-land-jurisdiction-validation-of-certain-partitions-of-mineral-rights-when-shares-of-two-or-more-laid-off-together",tabindex:"-1"},[t("§ 8.01-81 Who may compel partition of land; jurisdiction; validation of certain partitions of mineral rights; when shares of two or more laid off together. "),e("a",{class:"header-anchor",href:"#§-8-01-81-who-may-compel-partition-of-land-jurisdiction-validation-of-certain-partitions-of-mineral-rights-when-shares-of-two-or-more-laid-off-together","aria-label":'Permalink to "§ 8.01-81 Who may compel partition of land; jurisdiction; validation of certain partitions of mineral rights; when shares of two or more laid off together."'},"​")],-1),s=e("p",null,"Tenants in common, joint tenants, executors with the power to sell, and coparceners of real property, including mineral rights east and south of the Clinch River, shall be compellable to make partition and may compel partition, but in the case of an executor only if the power of sale is properly exercisable at that time under the circumstances; and a lien creditor or any owner of undivided estate in real estate may also compel partition for the purpose of subjecting the estate of his debtor or the rents and profits thereof to the satisfaction of his lien. Any court having general equity jurisdiction shall have jurisdiction in cases of partition, and in the exercise of such jurisdiction, shall order partition in kind if the real property in question is susceptible to a practicable division and may take cognizance of all questions of law affecting the legal title that may arise in any proceedings, between such tenants in common, joint tenants, executors with the power to sell, coparceners and lien creditors.",-1),l=e("p",null,"Any two or more of the parties, if they so elect, may have their shares laid off together when partition can be conveniently made in that way. If the court orders partition in kind, the court may require that one or more parties pay one or more parties' amounts so that the payments, taken together with the court-determined value of the in-kind distributions to the parties, will make the partition in kind just and proportionate in value to the fractional interests held. If the court orders partition in kind, the court shall allocate to the parties that are unknown, unlocatable, or the subject of a default judgment a part of the property representing the combined interests of such parties as determined by the court, and such part of the property shall remain undivided.",-1),h=e("p",null,"All partitions of mineral rights heretofore had are hereby validated.",-1),c=e("p",null,[t("Code 1950, § 8-690; 1964, c. 167; 1968, c. 412; 1977, c. 617; 1984, c. 226; 2020, cc. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0115",target:"_blank",rel:"noreferrer"},"115"),t(", "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0193",target:"_blank",rel:"noreferrer"},"193"),t(".")],-1),d=[n,s,l,h,c];function p(f,m,u,g,y,_){return i(),a("div",null,d)}const b=o(r,[["render",p]]);export{v as __pageData,b as default};
