import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 8.01-83.2 Notice by posting.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.01-83.2.md","filePath":"vacode/8.01-83.2.md"}'),i={name:"vacode/8.01-83.2.md"},o=n('<h1 id="§-8-01-83-2-notice-by-posting" tabindex="-1">§ 8.01-83.2 Notice by posting. <a class="header-anchor" href="#§-8-01-83-2-notice-by-posting" aria-label="Permalink to &quot;§ 8.01-83.2 Notice by posting.&quot;">​</a></h1><p>If the plaintiff in a partition action seeks an order of publication pursuant to § <a href="/vacode/8.01-316/">8.01-316</a>, the plaintiff, not later than 10 days after the court&#39;s determination, shall post and maintain while the action is pending a conspicuous sign on the property that is the subject of the action. The sign shall state that the action has commenced and identify the name and address of the court and the common designation by which the property is known. The court may require the plaintiff to publish on the sign the name of the plaintiff and the known defendants.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0115" target="_blank" rel="noreferrer">115</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0193" target="_blank" rel="noreferrer">193</a>.</p>',3),r=[o];function s(c,h,p,d,l,f){return t(),a("div",null,r)}const u=e(i,[["render",s]]);export{g as __pageData,u as default};
