import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 8.2-326 Sale on approval and sale or return; rights of creditors.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.2-326.md","filePath":"vacode/8.2-326.md"}'),o={name:"vacode/8.2-326.md"},s=a('<h1 id="§-8-2-326-sale-on-approval-and-sale-or-return-rights-of-creditors" tabindex="-1">§ 8.2-326 Sale on approval and sale or return; rights of creditors. <a class="header-anchor" href="#§-8-2-326-sale-on-approval-and-sale-or-return-rights-of-creditors" aria-label="Permalink to &quot;§ 8.2-326 Sale on approval and sale or return; rights of creditors.&quot;">​</a></h1><p>(1) Unless otherwise agreed, if delivered goods may be returned by the buyer even though they conform to the contract, the transaction is</p><p>(a) a &quot;sale on approval&quot; if the goods are delivered primarily for use; and</p><p>(b) a &quot;sale or return&quot; if the goods are delivered primarily for resale.</p><p>(2) Goods held on approval are not subject to the claims of the buyer&#39;s creditors until acceptance; goods held on sale or return are subject to such claims while in the buyer&#39;s possession.</p><p>(3) Any &quot;or return&quot; term of a contract for sale is to be treated as a separate contract for sale within the statute of frauds section of this title (§ <a href="http://law.lis.virginia.gov/vacode/8.2-201/" target="_blank" rel="noreferrer">8.2-201</a>) and as contradicting the sale aspect of the contract within the provisions of this title on parol or extrinsic evidence (§ <a href="http://law.lis.virginia.gov/vacode/8.2-202/" target="_blank" rel="noreferrer">8.2-202</a>).</p><p>1964, c. 219; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1007" target="_blank" rel="noreferrer">1007</a>.</p>',7),n=[s];function i(l,c,d,p,h,f){return r(),t("div",null,n)}const v=e(o,[["render",i]]);export{_ as __pageData,v as default};
