import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse(`{"title":"§ 8.2-708 Seller's damages for nonacceptance or repudiation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.2-708.md","filePath":"vacode/8.2-708.md"}`),o={name:"vacode/8.2-708.md"},n=r('<h1 id="§-8-2-708-seller-s-damages-for-nonacceptance-or-repudiation" tabindex="-1">§ 8.2-708 Seller&#39;s damages for nonacceptance or repudiation. <a class="header-anchor" href="#§-8-2-708-seller-s-damages-for-nonacceptance-or-repudiation" aria-label="Permalink to &quot;§ 8.2-708 Seller&#39;s damages for nonacceptance or repudiation.&quot;">​</a></h1><p>(1) Subject to subsection (2) and to the provisions of this title with respect to proof of market price (§ <a href="http://law.lis.virginia.gov/vacode/8.2-723/" target="_blank" rel="noreferrer">8.2-723</a>), the measure of damages for nonacceptance or repudiation by the buyer is the difference between the market price at the time and place for tender and the unpaid contract price together with any incidental damages provided in this title (§ <a href="http://law.lis.virginia.gov/vacode/8.2-710/" target="_blank" rel="noreferrer">8.2-710</a>), but less expenses saved in consequence of the buyer&#39;s breach.</p><p>(2) If the measure of damages provided in subsection (1) is inadequate to put the seller in as good a position as performance would have done then the measure of damages is the profit (including reasonable overhead) which the seller would have made from full performance by the buyer, together with any incidental damages provided in this title (§ <a href="http://law.lis.virginia.gov/vacode/8.2-710/" target="_blank" rel="noreferrer">8.2-710</a>), due allowance for costs reasonably incurred and due credit for payments or proceeds of resale.</p><p>1964, c. 219.</p>',4),i=[n];function s(c,d,l,p,h,f){return a(),t("div",null,i)}const m=e(o,[["render",s]]);export{_ as __pageData,m as default};
