import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 8.3A-118.1 Statute of limitations on deposit accounts and certificates of deposit.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.3A-118.1.md","filePath":"vacode/8.3A-118.1.md"}'),i={name:"vacode/8.3A-118.1.md"},r=o('<h1 id="§-8-3a-118-1-statute-of-limitations-on-deposit-accounts-and-certificates-of-deposit" tabindex="-1">§ 8.3A-118.1 Statute of limitations on deposit accounts and certificates of deposit. <a class="header-anchor" href="#§-8-3a-118-1-statute-of-limitations-on-deposit-accounts-and-certificates-of-deposit" aria-label="Permalink to &quot;§ 8.3A-118.1 Statute of limitations on deposit accounts and certificates of deposit.&quot;">​</a></h1><p>A. An action to enforce the obligations of a bank to pay all or part of the balance of a deposit account or certificate of deposit (collectively, a deposit) must be commenced within six years after the earlier of the following:</p><p>(1) If the deposit is a certificate of deposit to which subsection (e) of § <a href="http://law.lis.virginia.gov/vacode/8.3A-118/" target="_blank" rel="noreferrer">8.3A-118</a> applies, the date the six-year limitations period begins to run under subsection (e) of § <a href="http://law.lis.virginia.gov/vacode/8.3A-118/" target="_blank" rel="noreferrer">8.3A-118</a>; or</p><p>(2) The later of:</p><p>(A) The due date of the deposit indicated in the bank&#39;s last written notice of renewal;</p><p>(B) The date of the last written communication from the bank recognizing the bank&#39;s obligation with respect to the deposit; or</p><p>(C) The last day of the taxable year for which the owner of the deposit or the bank last reported interest income earned on the deposit for federal or state income tax purposes.</p><p>B. This section shall apply to negotiable and non-negotiable certificates of deposit.</p><p>2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0624" target="_blank" rel="noreferrer">624</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0083" target="_blank" rel="noreferrer">83</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0640" target="_blank" rel="noreferrer">640</a>.</p>',9),n=[r];function s(c,l,p,f,d,h){return t(),a("div",null,n)}const u=e(i,[["render",s]]);export{g as __pageData,u as default};
