import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 8.5A-112 Transfer of letter of credit.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.5A-112.md","filePath":"vacode/8.5A-112.md"}'),o={name:"vacode/8.5A-112.md"},i=a('<h1 id="§-8-5a-112-transfer-of-letter-of-credit" tabindex="-1">§ 8.5A-112 Transfer of letter of credit. <a class="header-anchor" href="#§-8-5a-112-transfer-of-letter-of-credit" aria-label="Permalink to &quot;§ 8.5A-112 Transfer of letter of credit.&quot;">​</a></h1><p>(a) Except as otherwise provided in § <a href="http://law.lis.virginia.gov/vacode/8.5A-113/" target="_blank" rel="noreferrer">8.5A-113</a>, unless a letter of credit provides that it is transferable, the right of a beneficiary to draw or otherwise demand performance under a letter of credit may not be transferred.</p><p>(b) Even if a letter of credit provides that it is transferable, the issuer may refuse to recognize or carry out a transfer if:</p><p>(1) the transfer would violate applicable law; or</p><p>(2) the transferor or transferee has failed to comply with any requirement stated in the letter of credit or any other requirement relating to transfer imposed by the issuer which is within the standard practice referred to in § <a href="http://law.lis.virginia.gov/vacode/8.5A-108/" target="_blank" rel="noreferrer">8.5A-108</a>(e) or is otherwise reasonable under the circumstances.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0343" target="_blank" rel="noreferrer">343</a>.</p>',6),n=[i];function s(f,c,d,l,h,p){return r(),t("div",null,n)}const u=e(o,[["render",s]]);export{m as __pageData,u as default};
