import{_ as e,o as t,c as r,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 8.5A-117.1 Security interest of issuer or nominated person.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.5A-117.1.md","filePath":"vacode/8.5A-117.1.md"}'),o={name:"vacode/8.5A-117.1.md"},i=n('<h1 id="§-8-5a-117-1-security-interest-of-issuer-or-nominated-person" tabindex="-1">§ 8.5A-117.1 Security interest of issuer or nominated person. <a class="header-anchor" href="#§-8-5a-117-1-security-interest-of-issuer-or-nominated-person" aria-label="Permalink to &quot;§ 8.5A-117.1 Security interest of issuer or nominated person.&quot;">​</a></h1><p>(a) An issuer or nominated person has a security interest in a document presented under a letter of credit to the extent that the issuer or nominated person honors or gives value for the presentation.</p><p>(b) So long as and to the extent that an issuer or nominated person has not been reimbursed or has not otherwise recovered the value given with respect to a security interest in a document under subsection (a), the security interest continues and is subject to Title 8.9A, but:</p><p>(1) a security agreement is not necessary to make the security interest enforceable under subdivision (b) (3) of § <a href="http://law.lis.virginia.gov/vacode/8.9A-203/" target="_blank" rel="noreferrer">8.9A-203</a>;</p><p>(2) if the document is presented in a medium other than a written or other tangible medium, the security interest is perfected; and</p><p>(3) if the document is presented in a written or other tangible medium and is not a certificated security, chattel paper, a document of title, an instrument, or a letter of credit, the security interest is perfected and has priority over a conflicting security interest in the document so long as the debtor does not have possession of the document.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1007" target="_blank" rel="noreferrer">1007</a>.</p>',7),s=[i];function a(c,d,u,p,h,l){return t(),r("div",null,s)}const _=e(o,[["render",a]]);export{f as __pageData,_ as default};
