import{_ as e,o as t,c as n,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 8.7-105.1 Reissuance in alternative medium.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.7-105.1.md","filePath":"vacode/8.7-105.1.md"}'),a={name:"vacode/8.7-105.1.md"},s=o('<h1 id="§-8-7-105-1-reissuance-in-alternative-medium" tabindex="-1">§ 8.7-105.1 Reissuance in alternative medium. <a class="header-anchor" href="#§-8-7-105-1-reissuance-in-alternative-medium" aria-label="Permalink to &quot;§ 8.7-105.1 Reissuance in alternative medium.&quot;">​</a></h1><p>(1) Upon request of a person entitled under an electronic document, the issuer of the electronic document may issue a tangible document as a substitute for the electronic document if:</p><p>(a) the person entitled under the electronic document surrenders control of the document to the issuer; and</p><p>(b) the tangible document when issued contains a statement that it is issued in substitution for the electronic document.</p><p>(2) Upon issuance of a tangible document in substitution for an electronic document in accordance with subsection (1):</p><p>(a) the electronic document ceases to have any effect or validity; and</p><p>(b) the person that procured issuance of the tangible document warrants to all subsequent persons entitled under the tangible document that the warrantor was a person entitled under the electronic document when the warrantor surrendered control of the electronic document to the issuer.</p><p>(3) Upon request of a person entitled under a tangible document, the issuer of the tangible document may issue an electronic document as a substitute for the tangible document if:</p><p>(a) the person entitled under the tangible document surrenders possession of the document to the issuer; and</p><p>(b) the electronic document when issued contains a statement that it is issued in substitution for the tangible document.</p><p>(4) Upon issuance of an electronic document in substitution for a tangible document in accordance with subsection (3):</p><p>(a) the tangible document ceases to have any effect or validity; and</p><p>(b) the person that procured issuance of the electronic document warrants to all subsequent persons entitled under the electronic document that the warrantor was a person entitled under the tangible document when the warrantor surrendered possession of the tangible document to the issuer.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0200" target="_blank" rel="noreferrer">200</a>.</p>',14),i=[s];function r(c,u,d,l,h,p){return t(),n("div",null,i)}const b=e(a,[["render",r]]);export{f as __pageData,b as default};
