import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 8.9A-110 Security interests arising under Title 8.2 or Title 8.2A.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.9A-110.md","filePath":"vacode/8.9A-110.md"}'),a={name:"vacode/8.9A-110.md"},n=i('<h1 id="§-8-9a-110-security-interests-arising-under-title-8-2-or-title-8-2a" tabindex="-1">§ 8.9A-110 Security interests arising under Title 8.2 or Title 8.2A. <a class="header-anchor" href="#§-8-9a-110-security-interests-arising-under-title-8-2-or-title-8-2a" aria-label="Permalink to &quot;§ 8.9A-110 Security interests arising under Title 8.2 or Title 8.2A.&quot;">​</a></h1><p>A security interest arising under §§ <a href="http://law.lis.virginia.gov/vacode/8.2-401/" target="_blank" rel="noreferrer">8.2-401</a>, <a href="http://law.lis.virginia.gov/vacode/8.2-505/" target="_blank" rel="noreferrer">8.2-505</a>, <a href="http://law.lis.virginia.gov/vacode/8.2-711/" target="_blank" rel="noreferrer">8.2-711</a> (3), or § <a href="http://law.lis.virginia.gov/vacode/8.2A-508/" target="_blank" rel="noreferrer">8.2A-508</a> (5) is subject to this title. However, until the debtor obtains possession of the goods:</p><p>(1) the security interest is enforceable, even if subdivision (b) (3) of § <a href="http://law.lis.virginia.gov/vacode/8.9A-203/" target="_blank" rel="noreferrer">8.9A-203</a> has not been satisfied;</p><p>(2) filing is not required to perfect the security interest;</p><p>(3) the rights of the secured party after default by the debtor are governed by Title 8.2 or Title 8.2A; and</p><p>(4) the security interest has priority over a conflicting security interest created by the debtor.</p><p>1964, c. 219, § 8.9-113; 1991, c. 536; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1007" target="_blank" rel="noreferrer">1007</a>.</p>',7),s=[n];function o(l,c,d,h,_,p){return t(),r("div",null,s)}const u=e(a,[["render",o]]);expo