import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 8.9A-209 Duties of secured party if account debtor has been notified of assignment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.9A-209.md","filePath":"vacode/8.9A-209.md"}'),i={name:"vacode/8.9A-209.md"},n=o('<h1 id="§-8-9a-209-duties-of-secured-party-if-account-debtor-has-been-notified-of-assignment" tabindex="-1">§ 8.9A-209 Duties of secured party if account debtor has been notified of assignment. <a class="header-anchor" href="#§-8-9a-209-duties-of-secured-party-if-account-debtor-has-been-notified-of-assignment" aria-label="Permalink to &quot;§ 8.9A-209 Duties of secured party if account debtor has been notified of assignment.&quot;">​</a></h1><p>(a) Applicability of section. Except as otherwise provided in subsection (c), this section applies if:</p><p>(1) there is no outstanding secured obligation; and</p><p>(2) the secured party is not committed to make advances, incur obligations, or otherwise give value.</p><p>(b) Duties of secured party after receiving demand from debtor. Within ten days after receiving an authenticated demand by the debtor, a secured party shall send to an account debtor that has received notification of an assignment to the secured party as assignee under § <a href="http://law.lis.virginia.gov/vacode/8.9A-406/" target="_blank" rel="noreferrer">8.9A-406</a> (a) an authenticated record that releases the account debtor from any further obligation to the secured party.</p><p>(c) Inapplicability to sales. This section does not apply to an assignment constituting the sale of an account, chattel paper, or payment intangible.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1007" target="_blank" rel="noreferrer">1007</a>.</p>',7),r=[n];function s(c,d,p,f,u,h){return t(),a("div",null,r)}const b=e(i,[["render",s]]);export{_ as __pageData,b as default};
